public class fahrenheitcelsius {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float Fahrenheit;
		float Celsius;
		Fahrenheit = 0;
		Celsius = ((Fahrenheit-32)*5)/9;
		System.out.println("Temperature in celsius is: "+Celsius);
	}
}
		
		
		
